mapper.update:
update member set
	user_image = #{user_image}
where user_email = #{user_email}

update member set
	user_nickname = #{user_nickname},
	user_pw = #{user_pw},
	user_phone = #{user_phone},
	user_intro = #{user_intro}
where user_email = #{user_email}

update member set 
	user_delete = #{user_delete}
where user_email = #{user_email}

select.if:
select * from board where
		(board_title like '%' || #{keyword} ||'%' or
		board_content like '%' || #{keyword} ||'%') and user_email=#{user_email}

select * from group_board where
		(title like '%' || #{keyword} ||'%' or
		content like '%' || #{keyword} ||'%') and user_email=#{user_email}

mapper.select:
select count(*) from
board
where user_email = #{user_email}

select count(*)
from group_board
where user_email = #{user_email}
